import zlib, base64, json

def decode_doc_crystal(crystal_string):
    try:
        compressed = base64.b64decode(crystal_string)
        # wbits=-15 for raw deflate
        json_bytes = zlib.decompress(compressed, wbits=-15)
        return json.loads(json_bytes.decode('utf-8'))
    except Exception as e:
        raise ValueError(f"Decode failed: {e}")

